(1, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Conectar ao WooCommerce.com', 'Conecte-se para receber notificações e atualizações importantes do produto.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-24 03:08:35', NULL, 0, 'plain', '', 0, 'info'),
(2, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Boas-vindas ao WooCommerce! Configure sua loja e comece a vender', 'Estamos aqui para ajudá-lo a executar as etapas mais importantes para colocar sua loja em funcionamento.', '{}', 'actioned', 'woocommerce-admin', '2020-09-24 03:08:57', NULL, 0, 'plain', '', 0, 'info'),
(3, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-09-24 03:14:15', NULL, 0, 'plain', '', 0, 'info'),
(4, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Services', 'WooCommerce Services helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2020-09-24 03:14:15', NULL, 0, 'plain', '', 0, 'info'),
(5, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-09-24 03:14:15', NULL, 0, 'plain', '', 0, 'info'),
(6, 'mailchimp-for-woocommerce-incomplete-install', 'warning', 'en_US', 'Mailchimp For WooCommerce', 'Plugin is not yet connected to a Mailchimp account. To complete the connection, open the settings page.', '{\"getting_started\":true,\"activated\":1600917289,\"activated_formatted\":\"September 24th\"}', 'unactioned', 'mailchimp-for-woocommerce', '2020-09-24 03:14:49', NULL, 0, 'plain', '', 0, 'info');
